import React, {useState} from "react";
import {Image, Popup, Rating, Button,Loader} from 'semantic-ui-react';
import {gql} from 'apollo-boost';
import { graphql} from 'react-apollo';
import Frame from './Frame';


const getElements = gql`{
  elements{
    img
    text
    name
    rate
    link
  }
}`;



function Element(props){

    const [togeled,setTogeled] = useState(true);
    console.log(props.data.elements)
    const navigate =(xx)=>{
        window.open(xx);
        console.log(xx)
    };
    
    
    let src='../img/elements/';

   

    if(props.data.loading){
        return(
            <Loader active inverted>loading ... </Loader>    
        )}
    else
    console.log(props.data)
        return(
            <div className="elements">
             {props.data.elements.map(elem =>{
                 return(
                <div id="element">
                <Popup
                    trigger={<Image  src={src +elem.img} size="tiny" className="img"></Image>}
                    on='click'
                    hideOnScroll>
                        
                    <Popup.Content>
                        <span>
                            <h3>{elem.name}</h3>
                            <p>{elem.text}</p>
                            <Rating defaultRating={elem.rate} maxRating={5} disabled />
                            <div className="btn_nav"><Button circular size="mini" icon="linkify" onClick={()=>navigate(elem.link)} color="facebook" color="facebook" target="iframe_a"/></div>
                        </span>
                    </Popup.Content>
                    
                </Popup>
                </div>)})}
                {togeled && <Frame  />}
            </div>
           
          
    )
}


export default graphql(getElements)(Element);

