import React from "react";
import {Image, Popup, Rating, Button} from 'semantic-ui-react';
import {gql} from 'apollo-boost';
import { graphql} from 'react-apollo';


const getElements = gql`{
  elements{
    img
    text
    name
    rate
    link
  }
}`;

function element(){
    const navigate =()=>{
        window.open(props.data.link);
    }
    let src='../img/elements/';
    return(
        
        <div id="element">
            
            <Popup
                trigger={<Image  src={src + props.data.img} size="tiny" className="img"></Image>}
                on='click'
                hideOnScroll>
                      
                <Popup.Content>
                    <span>
                        <h3>{props.data.name}</h3>
                        <p>{props.data.text}</p>
                        <Rating defaultRating={props.data.rate} maxRating={5} disabled />
                        <div className="btn_nav"><Button circular size="mini" icon="linkify" onClick={navigate} color="facebook"/></div>
                    </span>
                </Popup.Content>
                
            </Popup>
            </div>
    
    )
}


export default graphql(getElements)(element);
