import React ,{useState}from 'react';
import {Image,Input, Loader} from 'semantic-ui-react';
import Element from './components/element';

function App() {

  const logos=[
    {img:'tayara.png',text:"Tayara.tn sitevente en ligne",name:"Tayara.tn",rate:5},
    {img:'myTek.png',text:"Magasin d'informatique à Tunis",name:"Mytek",rate:3},
    {img:'sbs.png',text:" vente de matériel et accessoires informatique en Gros et en détails(spc Gaming)",name:"SBS informtique",rate:4.5} 
  ];
  const [loading,setLoading]=useState(false);
    setInterval(()=>{setLoading(true)},2000);

  if(loading){
  return (
    <div className="app">
        <div className="header">
              <div className='logo'>
                <Image src='../img/logo.png' size='small'/>
              </div>
              <div className='searchInput'>
                <Input icon={{ name: 'search', circular: true }} placeholder='Search...' />
              </div>
        </div>
        <div className="body">
            <div className="elements">
                {logos.map(logo =>{
                  return <Element data={logo}/>
                })}
            </div>


        </div>

    </div>
  );}
  else
  return(
    <div className="app">
        <div className="loader">
              <Loader active>Loading</Loader>
        </div>
    </div>
  )
}

export default App;
