import React,{useState, user} from "react";
import {Image,Button, Header, Placeholder, Popup} from 'semantic-ui-react';

const element=()=>{

    const [data, setData] = useState(null)
    const timer = useRef()
    return(
        <div id="element">
            <Popup
      on='click'
      onClose={() => {
        setData(null)
        clearTimeout(timer.current)
      }}
      onOpen={() => {
        setData(null)

        timer.current = setTimeout(() => {
          setData({
            description: "hahahahha",
            name:"tayara",
            title: "tayara",
          })
        }, 2000)
      }}
      popperDependencies={[!!data]}
      trigger={<Button content='Simulate loading' icon='lab' />}
      wide
    >
           <Image  src='../img/tayara.png' size="tiny">

           </Image>
    </Popup>
        </div>
    )
}


export default element;
