import React, {useState, useRef} from "react";
import {Image, Popup, Rating, Button,Loader} from 'semantic-ui-react';
import {gql} from 'apollo-boost';
import { graphql} from 'react-apollo';
import Frame from './Frame';


const getElements = gql`{
  elements{
    id
    img
    text
    name
    rate
    link
  }
}`;



function Element(props){
    const scrollToRef = (ref) => window.scrollTo(0, ref.current.offsetTop);
    const contentRef= useRef(null);
    const [togeled,setTogeled] = useState(false);
    const [link,setLink] = useState('');
    let src='../img/elements/';
    if(props.data.loading){
        return(
            <Loader active inverted>loading ... </Loader>    
        )}
    else
        return(
            <React.Fragment>
            <div className="elements">
            
             {props.data.elements.map(elem =>{
                 return(
                     <div>
                    <ItemsCarousel
                    // Placeholder configurations
                    enablePlaceholder
                    numberOfPlaceholderItems={5}
                    minimumPlaceholderTime={1000}
                    placeholderItem={<div style={{ height: 200, background: '#900' }}>Placeholder</div>}
            
                    // Carousel configurations
                    numberOfCards={3}
                    gutter={12}
                    showSlither={true}
                    firstAndLastGutter={true}
                    freeScrolling={false}
            
                    // Active item configurations
                    requestToChangeActive={this.changeActiveItem}
                    activeItemIndex={activeItemIndex}
                    activePosition={'center'}
            
                    chevronWidth={24}
                    rightChevron={'>'}
                    leftChevron={'<'}
                    outsideChevron={false}
                  >
                    <div id="element"  key={elem.id}>
                    <Popup
                        trigger={<Image  src={src +elem.img} size="tiny" className="img"></Image>}
                        on='click'
                        hideOnScroll>
                            
                        <Popup.Content>
                            <span>
                                <h3>{elem.name}</h3>
                                <p>{elem.text}</p>
                                <Rating defaultRating={elem.rate} maxRating={5} disabled />
                                <div className="btn_nav"><Button circular size="mini" icon="linkify" onClick={()=>{setTogeled(!togeled);setLink(elem.link);scrollToRef(contentRef)}} color="facebook" color="facebook"/></div>
                            </span>
                        </Popup.Content>
                    </Popup>
                    </div>
                  </ItemsCarousel>
                  </div>
             
             )})} 
            </div>
             <div ref={contentRef}>
                {togeled && [<Frame link={link} gg={true}/>,<a className="close_btn"onClick={()=>setTogeled(!togeled)}>close</a>]}
            </div>
            </React.Fragment>
    )
}
export default graphql(getElements)(Element);

