import React from "react";
import {Image, Popup, Rating, Button} from 'semantic-ui-react';
import {gql} from 'apollo-boost';
import { graphql} from 'react-apollo';


const getElements = gql`{
  elements{
    img
    text
    name
    rate
    link
  }
}`;



function element(props){
    console.log(props.data.elements)
    const navigate =(xx)=>{
        window.open(xx);
    };
    
    
    let src='../img/elements/';

   

    if(props.data.loading){
        return(
        <h1>Hi</h1>
        )}
    else
        return(
        <div id="element">
             {props.data.elements.map(elem =>{
                 return(
                <Popup
                    trigger={<Image  src={src + props.data.img} size="tiny" className="img"></Image>}
                    on='click'
                    hideOnScroll>
                        
                    <Popup.Content>
                        <span>
                            <h3>{elem.name}</h3>
                            <p>{elem.text}</p>
                            <Rating defaultRating={props.data.rate} maxRating={5} disabled />
                            <div className="btn_nav"><Button circular size="mini" icon="linkify" onClick={navigate(elem.lin)} color="facebook"/></div>
                        </span>
                    </Popup.Content>
                    
                </Popup>)})}
            </div>
    
    )
}


export default graphql(getElements)(element);
