import { Button, Grid, Header, Popup, Segment } from 'semantic-ui-react'

function Popupp(){
    const [state,setState] = useState({isOpen:true});
    const handleOpen = () => {
        setState({ isOpen: true })
      }
    
     const  handleClose = () => {
        setState({ isOpen: false })
      }
    return(
            <div>
                <h1></h1>
                <Popup
           
            content={`This message will self-destruct in seconds!`}
            on='click'
            open
            position='top right'
          />
            </div>
    )
}

export default Popupp;
