import React,{useState} from 'react';
import { Button, Popup }from 'semantic-ui-react'


function landingTip(){
    const timeoutLength = 2500
    
const [opened,setOpened]= useState({isOpen:true})
 const handleOpen = () => {
   setOpened({ isOpen: true })

       const  timeout = setTimeout(() => {
        setOpened({ isOpen: false })
    }, timeoutLength)
  }

 const handleClose = () => {
    setOpened({ isOpen: false })
    clearTimeout(timeout)
  }
    return(
        
        <div>
            <Popup
            trigger={<Button content='Open controlled popup' />}
            content={`This message will self-destruct in ${timeoutLength /
              1000} seconds!`}
            on='click'
            open={opened.isOpen}
            onClose={handleClose}
            onOpen={handleOpen}
            position='top right'
          />
             <h1>Heloo tip</h1>
        </div>
       
    )
} 


export default landingTip
